CREATE OR REPLACE FUNCTION obter_se_jejum_valido3 ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_min_p bigint, ie_tipo_item_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_w					bigint;
dt_horario_w				timestamp;

c01 CURSOR FOR
SELECT	dt_horario
from	prescr_dieta_hor
where	nr_prescricao 	= nr_prescricao_p
and		ie_tipo_item_p	= 'D'
and		nr_seq_dieta	> nr_seq_min_p

union

select	dt_horario
from	prescr_mat_hor
where	nr_prescricao 	= nr_prescricao_p
and		nr_seq_material > nr_seq_min_p
and		ie_tipo_item_p	= 'M';


BEGIN

open C01;
loop
fetch C01 into
	dt_horario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_w
	from	prescr_medica b,
			rep_jejum a
	where	b.nr_atendimento	= nr_atendimento_p
	and		b.nr_prescricao		= a.nr_prescricao
	and		coalesce(a.ie_suspenso,'N')	<> 'S'
	and		coalesce(b.dt_suspensao::text, '') = ''
	and		dt_horario_w between coalesce(a.dt_inicio, b.dt_inicio_prescr) and coalesce(a.dt_fim, b.dt_validade_prescr);

	if (nr_seq_w	> 0) then
		exit;
	end if;

end loop;
close C01;

if (nr_seq_w	= 0) then
	nr_seq_w	:= null;
end if;

return	nr_seq_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
