CREATE OR REPLACE FUNCTION obter_se_justifcativa_conv ( cd_convenio_p bigint, cd_material_p bigint, cd_setor_atendimento_p bigint, ie_tipo_Atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exige_w	varchar(1) := 'N';
cont_w		bigint;


BEGIN

select	/*+ index(a REPRCM_PK)*/		count(*)
into STRICT	cont_w
from	rep_regra_convenio_mat a
where	cd_material = cd_material_p;

if (cont_w > 0) then
	select	 /*+ index(a REPRCM_PK)*/			coalesce(max(ie_justificativa),'N')
	into STRICT		ie_exige_w
	from		rep_regra_convenio_mat a
	where	coalesce(cd_convenio,cd_convenio_p) = cd_convenio_p
	and		cd_material = cd_material_p
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_tipo_Atendimento,coalesce(ie_tipo_Atendimento_p,0)) = coalesce(ie_tipo_Atendimento_p,0);
end if;

return	ie_exige_w;

end;
