CREATE OR REPLACE FUNCTION obter_se_justif_atraso_quimio (nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
ie_dia_seguinte_w		varchar(1) := null;
dt_liberacao_w		timestamp;
dt_real_w		timestamp;
nr_seq_regra_w	bigint;
cd_estabelecimento_w	bigint;

c01 CURSOR FOR
SELECT 	nr_sequencia
from 	regra_justif_atraso
where 	coalesce(cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
order by 	coalesce(cd_estabelecimento, 0);


BEGIN

if (coalesce(nr_seq_atendimento_p,0) > 0) then

	select  max(dt_liberacao),
			max(dt_real),
			max(cd_estabelecimento)
	into STRICT    dt_liberacao_w,
		dt_real_w,
		cd_estabelecimento_w
	from 	paciente_atendimento
	where 	nr_seq_atendimento = nr_seq_atendimento_p;

	open c01;
	loop
	fetch c01 into
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;

	if (coalesce(nr_seq_regra_w,0) > 0)  then

		if (trunc(dt_real_w) = trunc(clock_timestamp())) then
			ie_dia_seguinte_w := 'N';
		elsif (trunc(dt_real_w) > trunc(clock_timestamp())) then
			ie_dia_seguinte_w := 'S';
		end if;

		if (ie_dia_seguinte_w IS NOT NULL AND ie_dia_seguinte_w::text <> '') then
			select  coalesce(max('N'),'S')
			into STRICT	ds_retorno_w
			from    regra_justif_atraso
			where   to_char(dt_liberacao_w,'hh24:mi') between to_char(hr_inicio_liberacao,'hh24:mi') and to_char(HR_FIM_LIBERACAO,'hh24:mi')
			and		to_char(dt_real_w,'hh24:mi') between to_char(hr_inicio_tratamento,'hh24:mi') and to_char(hr_fim_tratamento,'hh24:mi')
			and		coalesce(ie_dia_seguinte,'N') = ie_dia_seguinte_w
			and 	nr_sequencia = nr_seq_regra_w;
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
