CREATE OR REPLACE FUNCTION obter_se_justif_conv_proc ( nr_atendimento_p bigint, nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w			bigint := 0;
ie_exige_w			varchar(255) := 'N';
cont_w				bigint := 0;
cd_convenio_w		integer;


BEGIN

If (nr_atendimento_p > 0 ) then


	Select 	Obter_Convenio_Atendimento(nr_atendimento_p)
	into STRICT	cd_convenio_w
	;

	if (nr_seq_proc_interno_p > 0) then

		select	count(*)
		into STRICT	cont_w
		from	regra_convenio_plano a
		where	nr_seq_proc_interno = nr_seq_proc_interno_p
		and	cd_convenio = coalesce(cd_convenio_w,0)
		and	ie_regra = 6;

	end if;

	if ( cont_w = 0)  and ( cd_procedimento_p > 0 ) and ( ie_origem_proced_p > 0) then

		select	count(*)
		into STRICT	cont_w
		from	regra_convenio_plano a
		where	cd_procedimento = cd_procedimento_p
		and	nr_seq_proc_interno = nr_seq_proc_interno_p
		and	cd_convenio = coalesce(cd_convenio_w,0)
		and	ie_regra = 6;

	end if;

	if (cont_w > 0 ) then

		ie_exige_w := 'S';

	end if;

end if;

return	ie_exige_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
