CREATE OR REPLACE FUNCTION obter_se_justif_lib (nr_seq_justif_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w	bigint;
ds_retorno_w	varchar(1) := 'S';


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	rep_justif_proced_perfil
where	nr_seq_regra =  nr_seq_justif_p;

if (qt_registro_w > 0) then

	select	count(*)
	into STRICT	qt_registro_w
	from	rep_justif_proced_perfil
	where	nr_seq_regra =  nr_seq_justif_p
	and	cd_perfil = obter_perfil_ativo;

	if (qt_registro_w = 0) then
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
