CREATE OR REPLACE FUNCTION obter_se_justif_padrao_medic ( cd_material_p bigint, nr_seq_justificativa_p bigint, cd_estabelecimento_p bigint, ie_se_necessario_p text, ie_acm_p text) RETURNS varchar AS $body$
DECLARE


ie_mostrar_w				varchar(1) := 'S';
cd_grupo_material_w			bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
cd_medicamento_w			bigint;
ie_padronizado_w			varchar(5);
cd_material_estoque_w		bigint;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (nr_seq_justificativa_p IS NOT NULL AND nr_seq_justificativa_p::text <> '') then
	select 	cd_grupo_material,
			cd_subgrupo_material,
			cd_classe_material
	into STRICT	cd_grupo_material_w,
			cd_subgrupo_material_w,
			cd_classe_material_w
	from 	estrutura_material_v
	where 	cd_material	= cd_material_p;

	select 	max(cd_medicamento),
			max(obter_se_material_padronizado(cd_estabelecimento_p, cd_material_p)),
			max(cd_material_estoque)
	into STRICT	cd_medicamento_w,
			ie_padronizado_w,
			cd_material_estoque_w
	from 	material
	where 	cd_material	= cd_material_p;

	select	coalesce(max('S'), 'N')
	into STRICT	ie_mostrar_w
	from	rep_justificativa_material a
	where	((cd_medicamento_w IS NOT NULL AND cd_medicamento_w::text <> '') or (coalesce(a.ie_somente_cih, 'N') = 'N'))
	and		((ie_padronizado_w = 'N') or (coalesce(a.ie_somente_nao_padrao, 'N') = 'N'))
	and		coalesce(a.cd_classe_material, cd_classe_material_w)	= cd_classe_material_w
	and		coalesce(a.cd_subgrupo_material, cd_subgrupo_material_w) = cd_subgrupo_material_w
	and		coalesce(a.cd_grupo_material, cd_grupo_material_w)	= cd_grupo_material_w
	and		coalesce(cd_estab,coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
	and		coalesce(a.cd_material, cd_material_p)	= cd_material_p
	and		coalesce(a.cd_material_estoque, cd_material_estoque_w)	= cd_material_estoque_w
	and		coalesce(a.ie_situacao, 'A') = 'A'
	and		((coalesce(ie_somente_sn,'N') = 'N') or (ie_se_necessario_p	= 'S'))
	and		((coalesce(ie_somente_acm,'N') = 'N') or (ie_acm_p	= 'S'))
	and		nr_sequencia	= nr_seq_justificativa_p;
end if;

return	ie_mostrar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
