CREATE OR REPLACE FUNCTION obter_se_kit_materiais_pend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possuir_w	varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possuir_w
	from	prescr_medica a,
		prescr_material b
	where	a.nr_prescricao = b.nr_prescricao
	and	b.cd_motivo_baixa 	= 0
	and	b.ie_suspenso		= 'N'
	and	coalesce(a.dt_suspensao::text, '') = ''
	and	a.nr_atendimento 	= nr_atendimento_p
	and	(b.cd_material_kit IS NOT NULL AND b.cd_material_kit::text <> '')
	and	exists ( SELECT 1
		        from  cirurgia
		        where nr_prescricao = a.nr_prescricao);


end if;

return	ie_possuir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
