CREATE OR REPLACE FUNCTION obter_se_laudos_conta_lib (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(1):= 'S';			
qt_laudos_w	bigint:= 0;

BEGIN
 
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then 
 
	select	count(*) 
	into STRICT	qt_laudos_w 
	from  	laudo_paciente a, 
		atendimento_paciente b, 
		conta_paciente c 
	where  	a.nr_atendimento = b.nr_atendimento 
	and   	b.nr_atendimento = c.nr_atendimento 
	and 	c.nr_interno_conta = nr_interno_conta_p 
	and 	  coalesce(a.dt_liberacao::text, '') = '';
 
	if (qt_laudos_w > 0) then 
		ds_retorno_w := 'N';
	end if;
 
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
