CREATE OR REPLACE FUNCTION obter_se_laudo_anatomia ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE



ie_anatomia_w	varchar(1) := 'N';
nr_prescricao_w	bigint;
nr_seq_proc_w	bigint;


BEGIN

if (nr_sequencia_p > 0) then
	select	coalesce(nr_prescricao,0),
		coalesce(nr_seq_prescricao,0)
	into STRICT	nr_prescricao_w,
		nr_seq_proc_w
	from	laudo_paciente
	where	nr_sequencia	=	nr_sequencia_p;

	select	coalesce(max(obter_se_exame_anatomo_patol(nr_seq_exame)),'N')
	into STRICT	ie_anatomia_w
	from	prescr_procedimento
	where	nr_prescricao	=	nr_prescricao_w
	and	nr_sequencia	=	nr_seq_proc_w;

end if;

return	ie_anatomia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
