CREATE OR REPLACE FUNCTION obter_se_laudo_bloq ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_interno_p bigint) RETURNS bigint AS $body$
DECLARE


ie_resultado_w		bigint;


BEGIN

select	CASE WHEN coalesce(max(nr_prescricao)::text, '') = '' THEN  0  ELSE 1 END
into STRICT	ie_resultado_w
from 	controle_digitacao_laudo
where	nr_prescricao  = nr_prescricao_p   
and	nr_seq_prescr = nr_seq_prescr_p;

if (ie_resultado_w = 0) then
	select	CASE WHEN coalesce(max(nr_seq_interno)::text, '') = '' THEN  0  ELSE 1 END
	into STRICT	ie_resultado_w
	from 	 controle_ditar_laudo
	where	nr_seq_interno = nr_seq_interno_p;
end if;

if (ie_resultado_w = 0) then
 select CASE WHEN coalesce(max(nr_sequencia)::text, '') = '' THEN  0  ELSE 2 END
   into STRICT ie_resultado_w 
   from proc_atend_bloq_medico
  where nr_prescricao = nr_prescricao_p
    and nr_seq_prescricao = nr_seq_prescr_p;
end if;

return ie_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
