CREATE OR REPLACE FUNCTION obter_se_laudo_entregue_pac ( nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_entregue_w	varchar(1);


BEGIN

if (nr_seq_laudo_p IS NOT NULL AND nr_seq_laudo_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_entregue_w
	from	laudo_paciente a,
		laudo_paciente_loc b
	where	a.nr_sequencia = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_laudo_p
	and	b.ie_tipo_local = '1';
end if;

return	ie_entregue_w;

end;
