SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_laudo_exame_liberado ( nr_seq_exame_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		varchar(1);
nr_atendimento_w	bigint;
ie_anatomia_w		varchar(1) := 'N';
qt_exam_w		bigint;


BEGIN

/*
	EL - Exame Liberado
	TL - Todos os exames Liberados
*/
if (ie_opcao_p = 'EL') then

	if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then

		select 	max(coalesce(ie_anatomia_patologica,'N'))
		into STRICT	ie_anatomia_w
		from	exame_laboratorio
		where 	nr_seq_exame = nr_seq_exame_p;
	end if;

	if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') and (ie_anatomia_w <> 'S') then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_liberado_w
		from	result_laboratorio
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_prescricao = nr_seq_prescr_p;

		if (ie_liberado_w = 'N') then

			select	count(*)
			into STRICT	qt_exam_w
			from	prescr_procedimento
			where	nr_prescricao = nr_prescricao_p
			and	nr_seq_exame = nr_seq_exame_p;

			if (qt_exam_w = 1) then

				select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
				into STRICT	ie_liberado_w
				from	result_laboratorio
				where	nr_prescricao = nr_prescricao_p
				and	nr_seq_exame = nr_seq_exame_p;

			end if;

		end if;


	else

		select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_liberado_w
		from	laudo_paciente
		where	nr_prescricao = nr_prescricao_p
		and	nr_seq_prescricao = nr_seq_prescr_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

		if (ie_liberado_w = 'N') then
			begin
			select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_liberado_w
			from	procedimento_paciente a
			where	a.nr_sequencia_prescricao = nr_seq_prescr_p
			and	a.nr_prescricao = nr_prescricao_p
			and	exists (SELECT 1 from laudo_paciente b where b.nr_sequencia = a.nr_laudo and (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''));
			end;
		end if;


	end if;

elsif (ie_opcao_p = 'TL') then

	select	nr_atendimento
	into STRICT	nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	select  CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_liberado_w
	from	prescr_procedimento a,
		prescr_medica b
	where 	a.nr_prescricao = b.nr_prescricao
	and	b.nr_prescricao = nr_prescricao_p
	and	b.nr_atendimento = nr_atendimento_w
	and 	(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
	and 	a.ie_suspenso <> 'S'
	and	not exists (SELECT 1 from result_laboratorio w where a.nr_prescricao = w.nr_prescricao and a.nr_sequencia = w.nr_seq_prescricao);

	if (ie_liberado_w = 'S') then
		select  CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_liberado_w
		from	prescr_procedimento a,
			prescr_medica b
		where 	a.nr_prescricao = b.nr_prescricao
		and	b.nr_prescricao = nr_prescricao_p
		and	b.nr_atendimento = nr_atendimento_w
		and 	a.ie_suspenso <> 'S'
		and	not exists (SELECT 1 from laudo_paciente w where a.nr_prescricao = w.nr_prescricao and a.nr_sequencia = w.nr_seq_prescricao and (w.dt_liberacao IS NOT NULL AND w.dt_liberacao::text <> ''));
	end if;

end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_laudo_exame_liberado ( nr_seq_exame_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_opcao_p text) FROM PUBLIC;

