CREATE OR REPLACE FUNCTION obter_se_laudo_liberado (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		varchar(1);
dt_liberacao_w		timestamp;


BEGIN

if (nr_Seq_laudo_p IS NOT NULL AND nr_Seq_laudo_p::text <> '') then

	select  dt_liberacao
	into STRICT	dt_liberacao_w
	from	laudo_paciente
	where	nr_sequencia = nr_seq_laudo_p;

	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		ie_liberado_w := 'S';
	else
		ie_liberado_w := 'N';
	end if;

end if;

