CREATE OR REPLACE FUNCTION obter_se_laudo_patologia (nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_result_w 	varchar(1) := 'N';


BEGIN

if (nr_seq_laudo_p IS NOT NULL AND nr_seq_laudo_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_result_w
	from	laudo_paciente lp,
		procedimento_paciente 	pp,
		prescr_procedimento	pr,
		exame_laboratorio	ex
	where	lp.nr_laudo = nr_seq_laudo_p
	and	lp.nr_seq_proc	= pp.nr_sequencia
	and	pp.nr_prescricao= pr.nr_prescricao
	and	pp.nr_sequencia_prescricao = pr.nr_sequencia
	and	(pr.nr_seq_lab IS NOT NULL AND pr.nr_seq_lab::text <> '')
	and	pr.nr_seq_exame = ex.nr_seq_exame
	and	ex.ie_anatomia_patologica = 'S';

end if;

return	ie_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
