SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_laudo_pendente ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'S';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then
	begin

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from 	prescr_procedimento d,
		procedimento_paciente c,
		laudo_paciente a
	where 	d.nr_prescricao = nr_prescricao_p
	and	d.nr_sequencia	= nr_seq_prescr_p
	and   	c.nr_sequencia_prescricao = d.nr_sequencia
	and   	c.nr_prescricao = d.nr_prescricao
	and   	a.nr_seq_proc   = c.nr_sequencia
	and   	coalesce(a.dt_liberacao::text, '') = '';


	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_retorno_w
		from 	prescr_procedimento d,
			procedimento_paciente c,
			laudo_paciente a
		where 	d.nr_prescricao = nr_prescricao_p
		and	d.nr_sequencia	= nr_seq_prescr_p
		and   	c.nr_sequencia_prescricao = d.nr_sequencia
		and   	c.nr_prescricao = d.nr_prescricao
		and   	a.nr_seq_proc   = c.nr_sequencia;

	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
