CREATE OR REPLACE FUNCTION obter_se_laudo_urg ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(2);
qt_exame_w		  varchar(2);

 
 

BEGIN 
	select	count(*) 
	into STRICT	qt_exame_w 
	from 	prescr_procedimento d, 
		prescr_medica c, 
		pessoa_fisica d, 
		atendimento_paciente b, 
		laudo_paciente a 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	b.nr_atendimento	= nr_atendimento_p 
	and	a.nr_prescricao		= c.nr_prescricao 
	and	d.nr_prescricao		= c.nr_prescricao 
	and a.IE_URGENTE 		= 'S' 
	and	d.nr_sequencia		= a.nr_seq_prescricao 
	and	b.cd_pessoa_fisica	= d.cd_pessoa_fisica 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');
 
 
	if (qt_exame_w > 0) then 
		ds_retorno_w := 'S';
	else 
		ds_retorno_w := 'N';
		 
	end if;
	 
 
RETURN	ds_retorno_w;
 
END	;
$body$
