CREATE OR REPLACE FUNCTION obter_se_laudo_vigente ( cd_pessoa_fisica_p text, dt_referencia_p timestamp, ie_tipo_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


qt_laudo_w	bigint;
ds_retorno_w	varchar(1)	:= 'N';
ie_libera_w	varchar(1);
ie_tipo_convenio_w	bigint;

C01 CURSOR FOR
	SELECT	ie_tipo_convenio
	from	atendimento_paciente
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	order by nr_atendimento;


BEGIN

select	coalesce(vl_parametro,vl_parametro_padrao)
into STRICT	ie_libera_w
from	funcao_parametro
where	cd_funcao	= 0
and	nr_sequencia	= 83;

if (ie_tipo_laudo_p in (1,4)) then
	select	count(*)
	into STRICT	qt_laudo_w
	from	sus_laudo_paciente b,
		atendimento_paciente a
	where	a.nr_atendimento	= b.nr_atendimento
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((ie_libera_w = 'N') or (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''))
	and	b.ie_tipo_laudo_apac in (1,4)
	and	((trunc(dt_referencia_p) between trunc(b.dt_inicio_val_apac) and trunc(b.dt_fim_val_apac)) or (coalesce(b.dt_inicio_val_apac::text, '') = '') or (coalesce(b.dt_fim_val_apac::text, '') = ''));
else
	select	count(*)
	into STRICT	qt_laudo_w
	from	sus_laudo_paciente b,
		atendimento_paciente a
	where	a.nr_atendimento	= b.nr_atendimento
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((ie_libera_w = 'N') or (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> ''))
	and	b.ie_tipo_laudo_apac	= ie_tipo_laudo_p
	and	((trunc(dt_referencia_p) between trunc(b.dt_inicio_val_apac) and trunc(b.dt_fim_val_apac)) or (coalesce(b.dt_inicio_val_apac::text, '') = '') or (coalesce(b.dt_fim_val_apac::text, '') = ''));
end if;

open C01;
loop
fetch C01 into
	ie_tipo_convenio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (qt_laudo_w > 0) or (ie_tipo_convenio_w	<> 3) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
