SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_leito_atual_monit ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


/* 	Verifica Unidade Atual  	*/

/* Informacao
	M = Leito Monitorado
*/
ds_unidade_w			varchar(255);
ie_informacao_w			varchar(10);
nr_seq_interno_w			bigint;
cd_setor_atendimento_w		bigint;
cd_unidade_basica_w		varchar(010);
cd_unidade_compl_w		varchar(010);


BEGIN

ie_informacao_w := 'M';

if (coalesce(nr_atendimento_p,0) > 0) then
	begin
	nr_seq_interno_w	:= obter_atepacu_paciente(nr_atendimento_p, 'A');
	if (ie_informacao_w = 'M') then
		begin
		select 	a.cd_setor_atendimento,
			a.cd_unidade_basica,
			a.cd_unidade_compl
		into STRICT	cd_setor_atendimento_w,
			cd_unidade_basica_w,
			cd_unidade_compl_w
		from	atend_paciente_unidade a
		where	a.nr_seq_interno = nr_seq_interno_w;
		exception
			when others then
				cd_setor_atendimento_w	:= 0;
				cd_unidade_basica_w	:= '';
				cd_unidade_compl_w	:= '';
		end;

		begin
		select	coalesce(a.ie_leito_monitorado,'N')
		into STRICT	ds_unidade_w
		from	unidade_atendimento a
		where	a.cd_setor_atendimento	= cd_setor_atendimento_w
		and	a.cd_unidade_basica	= cd_unidade_basica_w
		and	a.cd_unidade_compl	= cd_unidade_compl_w;
		exception
			when others then
				ds_unidade_w	:= '';
		end;
	end if;
	end;
end if;

return ds_unidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
