CREATE OR REPLACE FUNCTION obter_se_leito_dispon_agenda (nr_seq_agenda_p bigint, nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE

														 
cont_w		bigint:=0;
qt_diaria_prev_w	smallint;
dt_agenda_w	timestamp;
ie_disponivel_w	varchar(1);
													

BEGIN 
 
select	trunc(dt_agenda), 
	coalesce(qt_diaria_prev,0) 
into STRICT	dt_agenda_w, 
	qt_diaria_prev_w 
from	agenda_paciente 
where	nr_sequencia = nr_seq_agenda_p;
 
while(cont_w <= qt_diaria_prev_w) loop 
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
	into STRICT	ie_disponivel_w 
	FROM	unidade_atendimento a		 
	WHERE	EXISTS (SELECT	1 
			FROM		gestao_vaga b 
			WHERE (dt_agenda_w+cont_w BETWEEN trunc(b.dt_prevista) AND trunc(b.dt_prevista)+coalesce(b.qt_dia,0)) 
			AND 		b.ie_status NOT IN ('D','N','C') 
			AND		a.cd_setor_atendimento 	= b.cd_setor_desejado 	 
			and		a.cd_unidade_basica 	= b.cd_unidade_basica 
			and		a.cd_unidade_compl	= b.cd_unidade_compl) 
	AND		a.nr_seq_interno 	= nr_seq_interno_p 
	and		a.ie_situacao = 'A';
		 
	if (ie_disponivel_w = 'N') then 
		exit;
	end if;	
	cont_w:=cont_w+1;
end loop;	
 
return	ie_disponivel_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_leito_dispon_agenda (nr_seq_agenda_p bigint, nr_seq_interno_p bigint) FROM PUBLIC;

