CREATE OR REPLACE FUNCTION obter_se_leito_livre ( cd_unidade_compl_p text, cd_unidade_basica_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_agrupamento_w	integer := 0;
ie_livre_w		varchar(1) := 'N';


BEGIN
if ((trim(both cd_unidade_compl_p) IS NOT NULL AND (trim(both cd_unidade_compl_p))::text <> '')) and ((trim(both cd_unidade_basica_p) IS NOT NULL AND (trim(both cd_unidade_basica_p))::text <> '')) and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
	begin

	select	coalesce(max(nr_agrupamento),0)
	into STRICT	nr_agrupamento_w
	from	unidade_atendimento
	where	cd_unidade_compl	= cd_unidade_compl_p
	and	cd_unidade_basica	= cd_unidade_basica_p
	and	cd_setor_atendimento	= cd_setor_atendimento_p;

	if (nr_agrupamento_w > 0) then
		begin

		select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_livre_w
		from	unidade_atendimento
		where	cd_setor_atendimento 	= cd_setor_atendimento_p
		and	nr_agrupamento		= nr_agrupamento_w
		and	ie_status_unidade	= 'L'
		and	((cd_unidade_basica	<> cd_unidade_basica_p)
			or cd_unidade_compl	<> cd_unidade_compl_p);
		end;
	end if;
	end;
end if;
return	ie_livre_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
