CREATE OR REPLACE FUNCTION obter_se_leito_sexo_acomp ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nr_atendimento_acomp_p bigint, DT_ACOMPANHANTE_P timestamp, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE




ie_sexo_compativel_w	varchar(1);
ie_sexo_unid_w		varchar(1);
ie_sexo_fixo_w		varchar(1);
ie_sexo_pf_w		varchar(1);


BEGIN

ie_sexo_compativel_w := 'S';

if (coalesce(nr_atendimento_acomp_p,0) > 0) then

	select	coalesce(ie_sexo_paciente,'L'),
		coalesce(ie_sexo_fixo,'N')
	into STRICT	ie_sexo_unid_w,
		ie_sexo_fixo_w
	from 	unidade_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_p
	and	cd_unidade_basica = cd_unidade_basica_p
	and	cd_unidade_compl  = cd_unidade_compl_p;

	select	max(a.ie_sexo)
	into STRICT	ie_sexo_pf_w
	from	pessoa_fisica a,
		atendimento_acompanhante b
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	b.nr_atendimento = nr_atendimento_acomp_p
	and a.cd_pessoa_fisica  = cd_pessoa_fisica_p;



	if	(((ie_sexo_fixo_w = 'S') and (upper(ie_sexo_unid_w) = upper(ie_sexo_pf_w))) or (ie_sexo_fixo_w = 'N') or (ie_sexo_unid_w in ('L','A'))) then

		ie_sexo_compativel_w := 'S';
	else
		ie_sexo_compativel_w := 'N';
	end if;

end if;

return	ie_sexo_compativel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
