CREATE OR REPLACE FUNCTION obter_se_leito_sexo_pac ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_sexo_compativel_w	varchar(1);
ie_sexo_unid_w			unidade_atendimento.ie_sexo_paciente%type;
cd_genero_unid_w		unidade_atendimento.NR_SEQ_GENERO%type;
ie_sexo_fixo_w			unidade_atendimento.ie_sexo_fixo%type;
ie_sexo_pf_w			pessoa_fisica.ie_sexo%type;
cd_genero_pf_w			pessoa_fisica_aux.nr_seq_genero%type;


BEGIN

ie_sexo_compativel_w := 'S';

if (coalesce(nr_atendimento_p,0) > 0) then

	select	coalesce(ie_sexo_paciente,'L'),
		coalesce(ie_sexo_fixo,'N'),
		(nr_seq_genero)
	into STRICT	ie_sexo_unid_w,
		ie_sexo_fixo_w,
		cd_genero_unid_w
	from 	unidade_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_p
	and	cd_unidade_basica = cd_unidade_basica_p
	and	cd_unidade_compl  = cd_unidade_compl_p;
	
	select	a.ie_sexo
	into STRICT	ie_sexo_pf_w
	from	pessoa_fisica a,
		atendimento_paciente b
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	b.nr_atendimento = nr_atendimento_p;
	
	select 	MAX(a.nr_seq_genero)
	into STRICT	cd_genero_pf_w
	from 	pessoa_fisica_aux a,
			atendimento_paciente b
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and		b.nr_atendimento = nr_atendimento_p;
	
	if	(((ie_sexo_fixo_w = 'S') and (upper(ie_sexo_unid_w) = upper(ie_sexo_pf_w)) and
		 ((cd_genero_pf_w = cd_genero_unid_w) OR coalesce(cd_genero_unid_w::text, '') = '')) or (ie_sexo_fixo_w = 'N') or (ie_sexo_unid_w in ('L','A'))) then
		
		ie_sexo_compativel_w := 'S';
	else
		ie_sexo_compativel_w := 'N';
	end if;

end if;

return	ie_sexo_compativel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
