CREATE OR REPLACE FUNCTION obter_se_leito_tem_respirador ( cd_setor_p bigint, cd_leito_b_p text, cd_leito_c_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w varchar(1) := 'N';

BEGIN

if (coalesce(cd_setor_p,0) > 0) then
	begin
		select	'S'
		into STRICT	ds_retorno_w
		from	unidade_atend_equip a,
			equipamento b
		where	b.cd_equipamento 	= a.cd_equipamento
		and	a.cd_setor_Atendimento 	= cd_setor_p
		and	a.cd_unidade_basica	= cd_leito_b_p
		and	a.cd_unidade_compl 	= cd_leito_c_p
		and	b.ie_tipo_equipamento 	= 'V'  LIMIT 1;
		
	exception
	when no_data_found then
		ds_retorno_w := 'N';
	end;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
