CREATE OR REPLACE FUNCTION obter_se_liberacao_tipo_equip ( nr_seq_equip_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_atualizacao_w		varchar(1);
qt_existe_w		bigint;
cd_perfil_w		integer;


C01 CURSOR FOR
SELECT	ie_atualizacao
from	man_tipo_equip_lib
where	nr_seq_tipo_equip = nr_seq_equip_p
and	((upper(nm_usuario_lib) = upper(nm_usuario_p))
	or (cd_perfil_lib = cd_perfil_w)
	or (coalesce(nm_usuario_lib::text, '') = '' and coalesce(cd_perfil_lib::text, '') = ''))
order by nm_usuario_lib desc, cd_perfil_lib desc, nr_sequencia;


BEGIN

cd_perfil_w		:= Obter_perfil_Ativo;
ie_atualizacao_w	:= 'S';

select	count(*)
into STRICT	qt_existe_w
from	man_tipo_equip_lib
where	nr_seq_tipo_equip = nr_seq_equip_p;

if (qt_existe_w = 0) then
	ie_atualizacao_w	:= 'S';
else
	open C01;
	loop
	fetch C01 into
		ie_atualizacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
end if;

return	ie_atualizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
