CREATE OR REPLACE FUNCTION obter_se_libera_ciclo ( nr_seq_paciente_p bigint, nr_ciclo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w    varchar(1) := 'S';


BEGIN

select   CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	 ie_permite_w
from     paciente_setor_lib
where    nr_seq_paciente   = nr_seq_paciente_p
and      nr_ciclo >= nr_ciclo_p;

return	ie_permite_w;

end;
$body$
