CREATE OR REPLACE FUNCTION obter_se_libera_filtro_eis (nr_seq_indicador_p bigint, ie_opcao_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE



qt_regra_w		bigint := 0;
ds_retorno_w	varchar(1) := 'S';


/*
ie_opcao_p			'A' - Atualiza filtros
				'L' - Limpa filtros
*/
BEGIN

select	count(*)
into STRICT	qt_regra_w
from 	usuario_regra_indicador
where	nr_seq_indicador = coalesce(nr_seq_indicador_p,0)
and		nm_usuario_lib	= nm_usuario_p;

if (qt_regra_w > 0) then

	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from 	usuario_regra_indicador
	where	nr_seq_indicador = nr_seq_indicador_p
	and		nm_usuario_lib	= nm_usuario_p
	and		((ie_limpa_filtro = 'S' and ie_opcao_p = 'L' )  or (ie_atualiza_filtro = 'S' and ie_opcao_p = 'A'));

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
