CREATE OR REPLACE FUNCTION obter_se_libera_isol_perfil ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_motivo_isolamento_w	bigint;
ie_retorno_w		varchar(1) := 'S';

BEGIN
if (coalesce(nr_atendimento_p,0) > 0) then

	select	coalesce((obter_motivo_isolamento(nr_atendimento_p,'C'))::numeric ,0)
	into STRICT	cd_motivo_isolamento_w
	;

	if (coalesce(cd_motivo_isolamento_w,0) > 0) then

		select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_retorno_w
		from	regra_motivo_isolamento
		where	nr_seq_motivo = cd_motivo_isolamento_w
		and	(cd_perfil IS NOT NULL AND cd_perfil::text <> '');

		if (ie_retorno_w = 'N') then

			select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_retorno_w
			from	regra_motivo_isolamento
			where	nr_seq_motivo = cd_motivo_isolamento_w
			and	cd_perfil  = obter_perfil_ativo;

		end if;

	end if;
end if;
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
