SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_libera_rep ( ds_inconsistencias_p text, ds_numero_p text) RETURNS varchar AS $body$
DECLARE


ds_inconsistencia_w	varchar(2000);
ds_inconsist_w		varchar(2000);
ie_consistir_w		varchar(2);
k			bigint;


BEGIN

ie_consistir_w	:= 'N';
ds_inconsistencia_w	:= ds_inconsistencias_p;
while	(ds_inconsistencia_w IS NOT NULL AND ds_inconsistencia_w::text <> '') LOOP
	select	position(',' in ds_inconsistencia_w)
	into STRICT	k
	;

	if (k > 1) and ((substr(ds_inconsistencia_w, 1, k -1) IS NOT NULL AND (substr(ds_inconsistencia_w, 1, k -1))::text <> '')) then
		ds_inconsist_w		:= substr(ds_inconsistencia_w, 1, k-1);
		if (ds_inconsist_w = ds_numero_p) then
			ie_consistir_w	:= 'S';
			ds_inconsistencia_w	:= null;
			exit;
		else
			ds_inconsistencia_w	:= substr(ds_inconsistencia_w, k + 1, 2000);

		end if;

	elsif (ds_inconsistencia_w IS NOT NULL AND ds_inconsistencia_w::text <> '') then
		ds_inconsist_w 		:= replace(ds_inconsistencia_w,',','');
		ds_inconsistencia_w	:= '';
	end if;

end loop;

return	ie_consistir_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
