CREATE OR REPLACE FUNCTION obter_se_lib_acomod_plano ( cd_convenio_p bigint, cd_plano_p text, cd_tipo_acomodacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1)	:= 'S';
qt_regra_acomod_w		integer;
qt_regra_acomod_plano_w		integer;


BEGIN
select	count(*)
into STRICT	qt_regra_acomod_w
from	regra_plano_acomodacao
where	cd_convenio	= cd_convenio_p
and 	ie_situacao = 'A';

if (qt_regra_acomod_w > 0) then

	select	count(*)
	into STRICT	qt_regra_acomod_plano_w
	from	regra_plano_acomodacao
	where	cd_convenio		= cd_convenio_p
	and 	cd_plano		= cd_plano_p
	and	cd_tipo_acomodacao	= cd_tipo_acomodacao_p
	and 	ie_situacao = 'A';

end if;

if (qt_regra_acomod_w > 0) and (qt_regra_acomod_plano_w = 0) then
	ds_retorno_w	:= 'N';
else
	ds_Retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
