SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_lib_ajuste_audit_pend (nr_seq_auditoria_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2);
nr_seq_pendencia_w	bigint;
nr_seq_estagio_w	bigint;
ie_permite_ajuste_w	varchar(1);
ie_permite_liberar_w	varchar(1);


BEGIN

ds_retorno_w	:= 'SS';

if (coalesce(nr_seq_auditoria_p,0) > 0) then

	select	max(nr_sequencia)
	into STRICT	nr_seq_pendencia_w
	from	cta_pendencia
	where	nr_seq_auditoria = nr_seq_auditoria_p;

	if (coalesce(nr_seq_pendencia_w,0) > 0) then

		select	max(nr_seq_estagio)
		into STRICT	nr_seq_estagio_w
		from	cta_pendencia_hist
		where	nr_seq_pend	= nr_seq_pendencia_w
		and	nr_sequencia = (	SELECT	max(nr_sequencia)
						from 	cta_pendencia_hist
						where 	nr_seq_pend	= nr_seq_pendencia_w);

		select	coalesce(max(ie_permite_ajuste),'S'),
			coalesce(max(ie_permite_liberar),'S')
		into STRICT	ie_permite_ajuste_w,
			ie_permite_liberar_w
		from	cta_estagio_pend
		where	nr_sequencia = nr_seq_estagio_w;

		ds_retorno_w	:= coalesce(ie_permite_ajuste_w,'S') || coalesce(ie_permite_liberar_w,'S');

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
