CREATE OR REPLACE FUNCTION obter_se_lib_aut_antimic ( cd_material_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, cd_intervalo_p text, cd_setor_atendimento_p bigint, ie_objetivo_filtro_p text default null) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		char(1);
ie_ctrl_medico_antimicrob_w	varchar(1);
nr_seq_familia_w		material_familia.nr_sequencia%type;
cd_grupo_material_w	grupo_material.cd_grupo_material%type;
cd_subgrupo_w		subgrupo_material.cd_subgrupo_material%type;
cd_material_estoque_w	material.cd_material%type;
nr_seq_ficha_tecnica_w	medic_ficha_tecnica.nr_sequencia%type;
cd_classe_material_w	classe_material.cd_classe_material%type;
ie_dia_atual_w		bigint;

c01 CURSOR FOR
SELECT	coalesce(ie_lib_auto,'N')
from	regra_antimicrobiano
where	cd_regra			= ie_ctrl_medico_antimicrob_w
and		cd_estabelecimento		= cd_estabelecimento_p
and		coalesce(cd_perfil,cd_perfil_p)	= cd_perfil_p
and		((coalesce(cd_intervalo::text, '') = '') or (cd_intervalo = cd_intervalo_p))
and		coalesce(cd_grupo_material, cd_grupo_material_w) = cd_grupo_material_w
and		coalesce(cd_subgrupo_material,cd_subgrupo_w) = cd_subgrupo_w
and		coalesce(nr_seq_familia, coalesce(nr_seq_familia_w,0)) = coalesce(nr_seq_familia_w,0)
and		coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
and		((coalesce(ie_dia_semana::text, '') = '') or (ie_dia_semana = ie_dia_atual_w))
and		coalesce(cd_material,cd_material_p)	= cd_material_p
and		coalesce(cd_material_estoque, coalesce(cd_material_estoque_w,0)) = coalesce(cd_material_estoque_w,0)
and		coalesce(nr_seq_ficha_tecnica, coalesce(nr_seq_ficha_tecnica_w,0)) = coalesce(nr_seq_ficha_tecnica_w,0)
and		coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p, 0)) = coalesce(cd_setor_atendimento_p, 0)
and		((ie_objetivo_filtro = ie_objetivo_filtro_p) or (coalesce(ie_objetivo_filtro::text, '') = ''))
and		((coalesce(dt_hora_inicio::text, '') = '') or (to_char(dt_hora_inicio,'hh24:mi:ss') < to_char(clock_timestamp(),'hh24:mi:ss')))
and		((coalesce(dt_hora_fim::text, '') = '') or (to_char(dt_hora_fim,'hh24:mi:ss') > to_char(clock_timestamp(),'hh24:mi:ss')))
order by
		coalesce(cd_material,0),
		coalesce(cd_material_estoque,0),
		coalesce(nr_seq_ficha_tecnica,0),
		coalesce(nr_seq_familia,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(cd_perfil,0),
		coalesce(ie_dia_semana,0),
		coalesce(to_char(dt_hora_inicio,'hh24:mi:ss'),'00:00:00'),
		coalesce(to_char(dt_hora_fim,'hh24:mi:ss'),'00:00:00'),
		coalesce(ie_objetivo_filtro,'X') desc,
		coalesce(cd_setor_atendimento,0);
	

BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	ie_ctrl_medico_antimicrob_w	:= substr(obter_ctrl_antimicrobiano_mat(cd_material_p),1,1);

	ie_dia_atual_w	:= Obter_Cod_Dia_Semana(clock_timestamp());

	select	max(cd_grupo_material),
			max(cd_subgrupo_material),
			max(cd_classe_material),
			max(nr_seq_familia),
			max(cd_material_estoque),
			max(nr_seq_ficha_tecnica)
	into STRICT	cd_grupo_material_w,
			cd_subgrupo_w,
			cd_classe_material_w,
			nr_seq_familia_w,
			cd_material_estoque_w,
			nr_seq_ficha_tecnica_w
	from	estrutura_material_v
	where 	cd_material	= cd_material_p;
		
	open C01;
	loop
	fetch C01 into	
		ie_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_retorno_w := ie_retorno_w;
		end;
	end loop;
	close C01;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
