SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_lib_baixa ( nr_titulo_p bigint, nr_seq_cheque_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
T = Título
CT = Controle Bancário/Tesouraria
*/
dt_lib_baixa_w		timestamp;
ie_lib_baixa_w		varchar(1)	:= 'S';
nr_seq_lib_baixa_w	varchar(255);
qt_registros_w		bigint;


BEGIN

if (ie_opcao_p = 'T') then
	begin

	select 	count(*)
	into STRICT	qt_registros_w
	from 	cobranca
	where 	nr_titulo = nr_titulo_p;


	if qt_registros_w > 0 then
		select	dt_lib_baixa
		into STRICT	dt_lib_baixa_w
		from	cobranca
		where	nr_titulo = nr_titulo_p;

		if (coalesce(dt_lib_baixa_w::text, '') = '') then
			ie_lib_baixa_w	:= 'N';
		end if;

	end if;

	exception
	when others then
		ie_lib_baixa_w	:= 'S';
	end;

elsif (ie_opcao_p = 'CT') then
	begin

	select 	count(*)
	into STRICT	qt_registros_w
	from 	cobranca
	where 	nr_seq_cheque = nr_seq_cheque_p;

	if qt_registros_w > 0 then
		select	dt_lib_baixa
		into STRICT	dt_lib_baixa_w
		from	cobranca
		where	nr_seq_cheque = nr_seq_cheque_p;

		if (coalesce(dt_lib_baixa_w::text, '') = '') then
			ie_lib_baixa_w	:= 'N';
		end if;
	end if;

	exception
	when others then
		ie_lib_baixa_w	:= 'S';
	end;

end if;

return	ie_lib_baixa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
