CREATE OR REPLACE FUNCTION obter_se_lib_cancel_conta ( nr_seq_motivo_cancel_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';
qt_regra_w	bigint;
qt_regra_conv_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	regra_lib_motivo_cancel
where	nr_seq_motivo = nr_seq_motivo_cancel_p;

if (qt_regra_w > 0) then
	select	count(*)
	into STRICT	qt_regra_conv_w
	from	regra_lib_motivo_cancel
	where	nr_seq_motivo = nr_seq_motivo_cancel_p
	and	cd_convenio = cd_convenio_p;

	if (qt_regra_conv_w = 0) then
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
