CREATE OR REPLACE FUNCTION obter_se_lib_declaracao_estab ( cd_declaracao_p text, cd_estab_agenda_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'S';
cd_estabelecimento_w	smallint;
qt_regra_w		bigint;
ie_util_estab_agenda_w	varchar(1) := 'N';


BEGIN

ie_util_estab_agenda_w := obter_param_usuario(871, 752, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_util_estab_agenda_w);

select	count(*)
into STRICT	qt_regra_w
from	declaracao_estab
where	cd_declaracao = cd_declaracao_p;

if (qt_regra_w > 0) then

	if  ((ie_util_estab_agenda_w = 'S') and (coalesce(cd_estab_agenda_p,0) > 0)) then
	     cd_estabelecimento_w := cd_estab_agenda_p;
	else
	     cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
	end if;

	select	count(*)
	into STRICT	qt_regra_w
	from	declaracao_estab
	where	cd_declaracao 		= cd_declaracao_p
	and	cd_estabelecimento	= cd_estabelecimento_w;

	if (qt_regra_w > 0) then
		ie_retorno_w	:= 'S';
	else
		ie_retorno_w	:= 'N';
	end if;

else
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
