CREATE OR REPLACE FUNCTION obter_se_lib_dieta ( nr_prescricao_p bigint, nr_seq_receita_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_receita_w	bigint;
ds_retorno		varchar(1);

c01 CURSOR FOR
	SELECT 	a.nr_sequencia
	FROM   	nut_receita a,
		NUT_DIETA_REC b
	WHERE  	a.nr_sequencia 	= b.nr_seq_receita
	AND	EXISTS (SELECT 	1
			FROM   	prescr_dieta x,
				NUT_ATEND_SERV_DIA_REP z,
				nut_atend_serv_dia y
			WHERE (x.cd_dieta 	 = b.cd_dieta
			or 	exists (select	1
					from	nut_grupo_producao_dieta c
					where	c.nr_seq_grupo_producao = b.nr_seq_grupo
					and	x.cd_dieta = c.cd_dieta))
			AND	y.nr_sequencia   = z.nr_seq_serv_dia
			AND	z.nr_prescr_oral = x.nr_prescricao
			AND	x.nr_prescricao  = nr_prescricao_p);


BEGIN

ds_retorno := 'N';

open 	c01;
loop
fetch 	c01 into
	nr_seq_receita_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	If (nr_seq_receita_p = nr_seq_receita_w)
	And (ds_retorno = 'N')then
		 ds_retorno := 'S';
	End if;
end loop;
close c01;

return	ds_retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_lib_dieta ( nr_prescricao_p bigint, nr_seq_receita_p bigint) FROM PUBLIC;

