CREATE OR REPLACE FUNCTION obter_se_lib_motivo_bloq (nr_seq_motivo_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);

C01 CURSOR FOR
	SELECT	coalesce(ie_liberado,'S')
	from	liberacao_motivo_bloqueio
	where	nr_seq_motivo = nr_seq_motivo_p
	and 	coalesce(cd_perfil, coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
	and 	coalesce(nm_usuario_regra, nm_usuario_p) = nm_usuario_p
	and 	ie_situacao = 'A'
	order by	coalesce(cd_perfil,0),
		coalesce(nm_usuario_regra,' ');


BEGIN

ie_retorno_w:= 'S';

open C01;
loop
fetch C01 into
	ie_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_retorno_w:= ie_retorno_w;
	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
