CREATE OR REPLACE FUNCTION obter_se_lib_movto_trans (nr_seq_saldo_caixa_p bigint, nr_seq_caixa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
qt_count_sim_w	bigint;
qt_count_nao_w	bigint;


BEGIN

ie_retorno_w	:= 'S';

select	count(*)
into STRICT	qt_count_sim_w
from	movto_trans_financ
where	nr_seq_saldo_caixa 	= nr_seq_saldo_caixa_p
and	nr_seq_caixa		= nr_seq_caixa_p
and	ie_rejeitado 		= 'S';

select	count(*)
into STRICT	qt_count_nao_w
from	movto_trans_financ
where	nr_seq_saldo_caixa 	= nr_seq_saldo_caixa_p
and	nr_seq_caixa		= nr_seq_caixa_p
and	coalesce(ie_rejeitado,'N') 	= 'N';

if (qt_count_sim_w > 0)  and (qt_count_nao_w > 0) then
	ie_retorno_w	:= 'N';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
