CREATE OR REPLACE FUNCTION obter_se_lib_nivel_atencao ( ie_nivel_atencao_p text, ie_niveis_lib_perfil_p text, nm_usuario_reg_p text, nr_prescricao_p bigint default 0, ie_nivel_p text default null) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(1):= 'N';
nm_usuario_w			varchar(15);
ie_nivel_atenc_w		varchar(10);
ie_nivel_perfil_usuario_w 	varchar(10);
ie_nivel_atencao_w		varchar(1);
cd_medico_w				varchar(10);
nm_usuario_reg_w		varchar(15);
ie_niveis_lib_perfil_w	varchar(255);

nm_usuario_lib_enf_w	prescr_medica.nm_usuario_lib_enf%type;
nm_usuario_presc_w		prescr_medica.nm_usuario%type;
nm_usuario_original_w	prescr_medica.nm_usuario_original%type;


BEGIN 
 
nm_usuario_w := wheb_usuario_pck.get_nm_usuario;
if (coalesce(ie_nivel_p::text, '') = '') then 
	ie_nivel_perfil_usuario_w := wheb_assist_pck.get_nivel_atencao_perfil;
else 
	ie_nivel_perfil_usuario_w	:= ie_nivel_p;
end if;
 
ie_nivel_atencao_w := ie_nivel_atencao_p;
nm_usuario_reg_w := nm_usuario_reg_p;
ie_niveis_lib_perfil_w := ie_niveis_lib_perfil_p;
 
if (coalesce(nr_prescricao_p,0) > 0) then 
 
	Select 	coalesce(max(ie_nivel_atencao),'T'), 
			max(nm_usuario_lib_enf), 
			max(nm_usuario), 
			max(nm_usuario_original) 
	into STRICT	ie_nivel_atencao_w, 
			nm_usuario_lib_enf_w, 
			nm_usuario_presc_w, 
			nm_usuario_original_w 
	from	prescr_medica 
	where 	nr_prescricao = nr_prescricao_p;
	 
	if (nm_usuario_lib_enf_w = nm_usuario_w) then 
		nm_usuario_reg_w := nm_usuario_lib_enf_w;
	elsif (nm_usuario_presc_w = nm_usuario_w) then 
		nm_usuario_reg_w := nm_usuario_presc_w;
	elsif (nm_usuario_original_w = nm_usuario_w) then 
		nm_usuario_reg_w := nm_usuario_original_w;
	end if;
end if;
 
 
if (ie_niveis_lib_perfil_w	<> 'L') then 
	ie_nivel_atenc_w := coalesce(ie_nivel_atencao_w,'T');
else 
	ie_nivel_atenc_w	:= ie_niveis_lib_perfil_w;
 
end if;
 
 
	if (ie_nivel_perfil_usuario_w = 'S') then 
		ie_niveis_lib_perfil_w := replace(ie_niveis_lib_perfil_w,'S','');
		ie_niveis_lib_perfil_w := replace(ie_niveis_lib_perfil_w,'X','S');
	elsif (coalesce(ie_nivel_perfil_usuario_w,'T') = 'T') then 
		ie_niveis_lib_perfil_w := ie_niveis_lib_perfil_w ||',T';
	end if;
 
	 
	if (obter_Se_Contido_char(ie_nivel_atenc_w, ie_niveis_lib_perfil_w) = 'S' or nm_usuario_reg_w = nm_usuario_w or (ie_nivel_perfil_usuario_w = 'S' and obter_se_grupo_lib(nm_usuario_reg_w) = 'S')) then 
		ds_retorno_w := 'S';
	else 
		ds_retorno_w := 'N';
	end if;
		 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
