SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_lib_nivel_inferior ( nr_titulo_p bigint, nm_usuario_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


/*Essa function Ã© utilizada dentro da proc GERAR_W_TITULO_PAGAR_LIBERACAO.*/

qt_usuarios_w	bigint;
qt_perfil_w		bigint;
ds_retorno_w	varchar(1);


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
		select 	count(*)
		into STRICT	qt_usuarios_w
		from   	conta_pagar_lib  a
		where  	a.nr_titulo = nr_titulo_p
		and	   	a.nm_usuario_lib = nm_usuario_p
		and	   	exists (SELECT 1
					   from	  conta_pagar_lib x
					   where  x.nr_titulo = a.nr_titulo
					   and	  (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
					   and	  x.ie_nivel < a.ie_nivel);
	end if;

	if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') then
		select 	count(*)
		into STRICT	qt_perfil_w
		from   	conta_pagar_lib  a
		where  	a.nr_titulo = nr_titulo_p
		and	   	a.cd_perfil = cd_perfil_p
		and	   	exists (SELECT 1
					   from	  conta_pagar_lib x
					   where  x.nr_titulo = a.nr_titulo
					   and	  (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
					   and	  x.ie_nivel < a.ie_nivel);
	end if;

end if;

if (qt_usuarios_w > 0) or (qt_perfil_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	coalesce(ds_retorno_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
