CREATE OR REPLACE FUNCTION obter_se_lib_por_tipo_atend (ie_tipo_atend_p bigint, ie_opcao_p bigint, vl_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            		varchar(1) := 'S';
qt_regra_tipo_atend_w   		bigint;
nr_seq_regra_w         		 bigint;

/*
ie_opcao_p
	1 - Tipo convênio.
	2 - TISS Tipo Atend
*/
BEGIN

If (ie_opcao_p = 1) then -- Tipo Convênio
        select 	coalesce(max(a.nr_sequencia),0)
        into STRICT   	nr_seq_regra_w
        from   	regra_lib_tipo_atend a,
		regra_lib_tipo_atend_conv b
        where  	a.ie_tipo_atendimento	 	= ie_tipo_atend_p
	and	a.nr_sequencia			= b.nr_seq_regra_atend
	and	a.ie_anatomia_patologica	= 'N';

        if (nr_seq_regra_w > 0) then
                select 	coalesce(max(1),0)
                into STRICT   	qt_regra_tipo_atend_w
                from    regra_lib_tipo_atend_conv
                where  	nr_seq_regra_w		= nr_seq_regra_atend
                and    	ie_tipo_convenio	= (vl_opcao_p)::numeric;

                if (qt_regra_tipo_atend_w > 0) then
                        ds_retorno_w := 'S';
                else    ds_retorno_w := 'N';
                end if;
        end if;
Elsif (ie_opcao_p = 2) then -- TISS Tipo  Atend
        select 	coalesce(max(nr_sequencia),0)
        into STRICT   	nr_seq_regra_w
        from   	regra_lib_tipo_atend
        where  	ie_tipo_atendimento		= ie_tipo_atend_p
	and	ie_anatomia_patologica		= 'N';

        if (nr_seq_regra_w > 0) then
                select 	coalesce(max(1),0)
                into STRICT   	qt_regra_tipo_atend_w
                from    regra_lib_tipo_atend_tiss
                where  	nr_seq_regra_w	= nr_seq_regra_atend
                and    	ie_tipo_atend_tiss	= (vl_opcao_p)::numeric;

                if (qt_regra_tipo_atend_w > 0) then
                        ds_retorno_w := 'S';
                else    ds_retorno_w := 'N';
                end if;
        end if;
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
