CREATE OR REPLACE FUNCTION obter_se_lib_prescr_horario ( dt_inicial_p timestamp, dt_final_p timestamp, dt_liberacao_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_inicial_w    timestamp;
dt_final_w      timestamp;
ie_vigente_w    varchar(1);


BEGIN

if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') then
	dt_inicial_w	:= ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_liberacao_p, dt_inicial_p);
else
	dt_inicial_w	:= null;
end if;

if (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
	dt_final_w	:= ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_liberacao_p, dt_final_p);
else
	dt_final_w	:= null;
end if;

if (dt_final_w IS NOT NULL AND dt_final_w::text <> '') and (dt_inicial_w IS NOT NULL AND dt_inicial_w::text <> '') and (dt_liberacao_p between trunc(dt_liberacao_p) and dt_final_w) and (to_char(dt_inicial_w,'hh24:mi:ss') > to_char(dt_final_w,'hh24:mi:ss')) then
	dt_inicial_w := dt_inicial_w - 1;
end if;

if (dt_inicial_w IS NOT NULL AND dt_inicial_w::text <> '') and (dt_final_w IS NOT NULL AND dt_final_w::text <> '') and (to_char(dt_inicial_w,'hh24:mi:ss') > to_char(dt_final_w,'hh24:mi:ss')) then
	dt_final_w := dt_final_w + 1;
end if;

if (coalesce(dt_inicial_w::text, '') = '') or (dt_inicial_w <= dt_liberacao_p) then
	ie_vigente_w := 'S';
else
	ie_vigente_w := 'N';
end if;


if (ie_vigente_w = 'S') and
	((coalesce(dt_final_w::text, '') = '') or (dt_final_w >= dt_liberacao_p)) then
	ie_vigente_w := 'S';
else
	ie_vigente_w := 'N';
end if;


return ie_vigente_w;
end;
$body$
