CREATE OR REPLACE FUNCTION obter_se_lib_tipo_controle ( cd_tipo_controle_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_libera_w	varchar(1);

BEGIN

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_libera_w
from	controle_pessoa_perfil
where	cd_tipo_controle = cd_tipo_controle_p;

if (ie_libera_w = 'N') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_libera_w
	from	controle_pessoa_perfil
	where	coalesce(cd_tipo_controle,cd_tipo_controle_p) 	= cd_tipo_controle_p
	and	coalesce(cd_perfil,cd_perfil_p) 		= cd_perfil_p;
end if;

return	ie_libera_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
