CREATE OR REPLACE FUNCTION obter_se_link_liberado (nr_seq_link_p bigint, cd_pessoa_fisica_p text, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possui_regra_w	varchar(1);
ie_liberado_w		varchar(1):= 'S';


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_possui_regra_w
from	link_base_dados_farm_lib
where	nr_seq_link = nr_seq_link_p;

if (ie_possui_regra_w = 'S') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_liberado_w
	from	link_base_dados_farm_lib
	where	nr_seq_link = nr_seq_link_p
	and	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_p)= cd_pessoa_fisica_p
	and	coalesce(cd_perfil,cd_perfil_p)		= cd_perfil_p;
end if;

return	ie_liberado_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
