CREATE OR REPLACE FUNCTION obter_se_livro_valido (dt_inicial_p timestamp, dt_final_p timestamp, nr_sequencia_p bigint default null, ie_tipo_livro_fiscal_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_registro_w	bigint;



BEGIN

select	count(*)
into STRICT	qt_registro_w
from	fis_livro_fiscal
where	((dt_inicial_p between dt_inicial and dt_final)
or (dt_final_p between dt_inicial and dt_final)
or (dt_inicial between dt_inicial_p and dt_final_p)
or (dt_final between dt_inicial_p and dt_final_p))
and	nr_sequencia <> nr_sequencia_p
and	ie_tipo_livro_fiscal = ie_tipo_livro_fiscal_p;

if (qt_registro_w > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
