CREATE OR REPLACE FUNCTION obter_se_local_lib_usuario ( nm_usuario_p text, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w			varchar(1) := 'N';
qt_existe_w			bigint;

BEGIN


select	count(*)
into STRICT	qt_existe_w
from	Usuario_Setor_v a,
	setor_atendimento b
where	a.cd_setor_atendimento	= b.cd_setor_atendimento
and	b.cd_local_estoque		= cd_local_estoque_p
and	a.nm_usuario		= nm_usuario_p;

if (qt_existe_w > 0) then
	ie_liberado_w := 'S';
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
