CREATE OR REPLACE FUNCTION obter_se_local_padrao_setor (cd_setor_atendimento_p bigint, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


ie_local_padrao_w	varchar(1):= 'S';
qt_regra_w		bigint;
qt_regra_perfil_w	bigint;
ie_libera_local_w	varchar(1):= 'N';

C01 CURSOR FOR
	SELECT 	coalesce(ie_libera_local,'S')
	from 	setor_local_regra
	where 	cd_setor_atendimento = cd_setor_atendimento_p
	and 	cd_local_estoque     = cd_local_estoque_p
	and 	coalesce(cd_perfil, coalesce(obter_perfil_ativo,0)) = coalesce(obter_perfil_ativo,0)
	and 	coalesce(ie_situacao,'A') = 'A'
	order by coalesce(cd_perfil,0);


BEGIN

select 	count(*)
into STRICT	qt_regra_w
from 	setor_local_regra
where 	cd_setor_atendimento = cd_setor_atendimento_p
and 	cd_local_estoque     = cd_local_estoque_p
and 	coalesce(ie_situacao,'A') = 'A';

if (qt_regra_w > 0) then

	open C01;
	loop
	fetch C01 into
		ie_libera_local_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_libera_local_w:= ie_libera_local_w;
		end;
	end loop;
	close C01;

	if (ie_libera_local_w = 'N') then
		ie_local_padrao_w:= 'N';
	end if;
end if;

return	ie_local_padrao_w;

end;
$body$
LANGUAGE PLPGSQL
