CREATE OR REPLACE FUNCTION obter_se_local_permite_solic ( nr_seq_local_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';


BEGIN

if (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') then

	select	coalesce(max(ie_permite_solicitacao),'S')
	into STRICT	ds_retorno_w
	from	same_local
	where	nr_sequencia = nr_seq_local_p;

end if;

return	ds_retorno_w;

end;
$body$
