CREATE OR REPLACE FUNCTION obter_se_local_setor_baixa ( nr_devolucao_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
cd_local_estoque_w	smallint;


BEGIN

if (coalesce(nr_devolucao_p,0) > 0) then

	select	max(cd_local_estoque)
	into STRICT	cd_local_estoque_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_p;

	select	coalesce(max('N'),'S')
	into STRICT	ie_retorno_w
	from	item_devolucao_material_pac
	where	nr_devolucao = nr_devolucao_p
	and	cd_local_estoque <> cd_local_estoque_w;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
