CREATE OR REPLACE FUNCTION obter_se_local_setor_lib (nr_seq_local_p text) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		varchar(1) := 'S';
qt_reg_w		bigint;
cd_perfil_w		integer;
cd_setor_atendimento_w	integer;
nm_usuario_lib_w	varchar(15);


BEGIN

cd_perfil_w 		:= obter_perfil_Ativo;
cd_setor_atendimento_w  := obter_setor_ativo;
nm_usuario_lib_w	:= obter_usuario_ativo;

select	count(*)
into STRICT	qt_reg_w
from	pa_local_lib
where	nr_seq_local = nr_seq_local_p;

if (qt_reg_w > 0) then
	select	count(*)
	into STRICT	qt_reg_w
	from	pa_local_lib
	where	nr_seq_local = nr_seq_local_p
	and	coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w
	and	coalesce(nm_usuario_lib,nm_usuario_lib_w) = nm_usuario_lib_w;

	if (qt_reg_w = 0) then
		ie_liberado_w	:= 'N';
	end if;
end if;

return	ie_liberado_w;

end;
$body$
