SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_loc_distribuidor (nr_seq_loc_os_p bigint, nr_seq_loc_distribuidor_p bigint,cd_cnpj_dist_p text) RETURNS varchar AS $body$
DECLARE


qt_reg_w 	bigint;
cd_cnpj_w	varchar(14);
ie_retorno_w 	varchar(1) := 'N';


BEGIN

if (nr_seq_loc_os_p = nr_seq_loc_distribuidor_p) then
	ie_retorno_w := 'S';
else
	if (coalesce(cd_cnpj_dist_p::text, '') = '' ) then
		select	cd_cnpj
		into STRICT	cd_cnpj_w
		from	man_localizacao
		where	nr_sequencia = nr_seq_loc_distribuidor_p;
	else
		cd_cnpj_w := cd_cnpj_dist_p;
	end if;

	select 	count(*)
	into STRICT	qt_reg_w
	FROM	com_canal_cliente a,
		com_cliente b,
		com_canal c,
		man_localizacao d
	WHERE	d.cd_cnpj = b.cd_cnpj
	AND	c.cd_cnpj = cd_cnpj_w
	and	d.nr_sequencia = nr_seq_loc_os_p
	AND	a.nr_seq_canal = c.nr_sequencia
	AND	a.ie_tipo_atuacao = 'V'
	AND	b.nr_sequencia = a.nr_seq_cliente
	AND	b.ie_fase_venda = 'F'
	AND	b.ie_resp_atend = 'D'
	AND	b.ie_classificacao = 'C'
	AND	coalesce(a.dt_fim_atuacao::text, '') = '';

	if (qt_reg_w = 0 ) then
		SELECT	count(*)
		into STRICT	qt_reg_w
		FROM	com_canal_cliente a,
			com_cliente b,
			com_canal c,
			man_localizacao d
		WHERE EXISTS (SELECT	1
			FROM	man_localizacao_pj f
			WHERE	d.nr_sequencia = f.nr_seq_localizacao
			AND	f.cd_cnpj = b.cd_cnpj)
		AND	c.cd_cnpj = cd_cnpj_w
		and	d.nr_sequencia = nr_seq_loc_os_p
		AND	a.nr_seq_canal = c.nr_sequencia
		AND	a.ie_tipo_atuacao = 'V'
		AND	b.nr_sequencia = a.nr_seq_cliente
		AND	b.ie_fase_venda = 'F'
		AND	b.ie_resp_atend = 'D'
		AND	b.ie_classificacao = 'C'
		AND	coalesce(a.dt_fim_atuacao::text, '') = '';
	end if;

	if (qt_reg_w > 0 ) then
		ie_retorno_w := 'S';
	end if;
end if;
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
